mapper.insert:

    	select seq_order_from_branch_no.currval FROM DUAL

 	INSERT
		INTO order_from_branch
				( 
				order_from_branch_no
			,	branch_no
			,	statement_no
			, 	order_date
			, 	total_order_amount
			,	order_branch_status_code
				) 
		VALUES	 
				(
				seq_order_from_branch_no.nextval
			, 	#{branchNo}
			, 	#{statementNo}
			, 	#{orderDate}
			, 	#{orderFromBranchTotalAmount}
			, 	'01'
				)

 	INSERT
		INTO order_from_branch_product
			( 
		  	  order_branch_product_numbering
		  ,   order_from_branch_no
		  ,   product_no
		  ,   order_quantity
		  ,   order_price
		  ,   amount
		  ,   order_branch_prod_status_code
			) 
	VALUES	 
			(	
			seq_order_branch_prod.nextval
		, 	#{orderFromBranchNo}
		, 	#{productNo}
		,	#{orderFromBranchProductQuantity}
		, 	#{price}
		, 	#{orderFromBranchProductAmount}
		, 	'01'
			)

mapper.select:
SELECT ob.order_from_branch_no , ob.branch_no , ob.statement_no , ob.order_date , ob.total_order_amount , ob.order_branch_status_code , CODE_FUNCTION('orderBranch', order_branch_status_code)AS BRANCH_STATUS_CODE_NAME , BRANCH_NAME_FUNCTION(branch_no) AS fnc_branch_name , op.order_branch_product_numbering , op.order_from_branch_no , op.product_no , op.order_quantity , op.order_price , op.amount , op.order_branch_prod_status_code , CODE_FUNCTION('orderBranchItem', ORDER_BRANCH_PROD_STATUS_CODE)AS ORDER_PROD_STATUS_NAME , p.product_name FROM order_from_branch ob , order_from_branch_product op , product p
ob.order_from_branch_no = op.order_from_branch_no(+) AND op.product_no = p.product_no(+) AND ob.order_from_branch_no=#{searchKeyword}
ORDER BY ob.order_from_branch_no DESC

mapper.update:
UPDATE order_from_branch_product

	order_branch_prod_status_code = #{orderFromBranchProductStatusCodeNo}

WHERE order_from_branch_no = #{orderFromBranchNo}
	  AND product_no = #{productNo}
